mapper.select:
SELECT * FROM appoint_order WHERE 1=1
AND hospital_name=#{hospitalName}
AND dept_name=#{deptName}
AND period=#{period}
AND city=#{city}
AND life_type=1
AND order_status=#{orderStatus}
AND life_type=2
AND life_type=3
AND ( order_no LIKE CONCAT(CONCAT('%',#{ext1}), '%') OR hospital_name LIKE CONCAT(CONCAT('%',#{ext1}), '%') OR consultant_name LIKE CONCAT(CONCAT('%',#{ext1}), '%') OR patient_name LIKE CONCAT(CONCAT('%',#{ext1}), '%') OR treatment_hospital_address LIKE CONCAT(CONCAT('%',#{ext1}), '%') )
ORDER BY update_time DESC
